            <p class="bottomText">Login Facebok</p>
          </v-btn>
        </template>

        <template>
          <p class="textRegister"> - Ou Criar Conta - </p>
        </template>

        <div class="inputs">
         <v-row>
          <v-col
            cols="12"
            sm="6"
          >
            <v-text-field
              v-model="form.first"
              :rules="rules.name"
              color="purple darken-2"
              label="Seu nome"
              required
            ></v-text-field>
          </v-col>
         </v-row>
         <v-row>
            <v-col
              cols="12"
              sm="6"
            >
              <v-text-field
                v-model="email"
                :rules="[rules.required, rules.email]"
                label="E-mail"
              ></v-text-field>
            </v-col>
         </v-row>
         <v-row>
            <v-col
              cols="12"
              sm="6"
            >
              <v-text-field
                v-model="password"
                :append-icon="show1 ? 'mdi-eye' : 'mdi-eye-off'"
                :rules="[rules.required, rules.min]"
                :type="show1 ? 'text' : 'password'"
                name="input-10-1"
                label="Senha"
                hint="At least 8 characters"
                counter
                @click:append="show1 = !show1"
              ></v-text-field>
            </v-col>
         </v-row>
